import { useEffect, useState } from "react";



const bcAccount = (addValue) => {
    console.log('getting bank account')
    console.log(addValue + 100);
    return addValue + 100;
}


const UseMemoComponent = () => {
    const [bankAccount, setBankAccount] = useState();

    const sumResult = bcAccount(0);

    console.log(sumResult);

    const handleAddMoney = () => {
        bcAccount(bankAccount);
        setBankAccount(0)
    }

    const handleOnChange = (e) => {
        console.log(e.target.value)
        setBankAccount(e.target.value)
    }

    useEffect(() =>{
        sumResult = bcAccount(bankAccount);
    }, [setBankAccount])

    return <>
        <h1>Use Memo</h1>
        <h2>Bank AccountValue: {sumResult} </h2>
        <input type='number' value={bankAccount} onChange={handleOnChange} />
        <button onClick={handleAddMoney}>Add Value</button>
    </>
}

export default UseMemoComponent;
