import { useState } from "react";



const bcAccount = (addValue) => {
    console.log('getting bank account')
    return addValue + 100;
}


const UseMemoComponent = () => {
    const [bankAccount, setBankAccount] = useState();

    const sumResult = bcAccount(0);

    console.log(sumResult);

    const handleAddMoney = () => {
        setBankAccount(0)
    }

    const handleOnChange = (e) => {
        setBankAccount(e.target.value)
    }

    return <>
        <h1>Use Memo</h1>
        <input type='number' value={bankAccount} handleOnChange={(e) => handleOnChange(e)} />
        <button onClick={handleAddMoney}>Add Value</button>
    </>
}

export default UseMemoComponent;
