import { memo, useCallback, useState } from "react";

const UseCallbackComponent = () => {
    const [counter, setCounter] = useState(0);
    const [searchTerm, setSearchTerm] = useState([]);

    const handleClick = useCallback((value) => {
        setCounter(counter + 1)
        setSearchTerm([...searchTerm, value]);

        console.log(counter)
    }, searchTerm)


    return <>
        <h1>useCallback</h1>
        <h2>Times useCallback Changes {counter}</h2>
        <ChildComponent searchTerm={searchTerm} setSearchTerm={setSearchTerm} handleClick={handleClick}/>
    </>
}

const ChildComponent = memo(({ searchTerm, setSearchTerm, handleClick}) => {
    const [inputValue, setInputValue] = useState('');
    const handleOnChange = (e) => {
        setInputValue(e.target.value)
    }   

    return <div>
        <input type='text' onChange={(e) => handleOnChange(e)} />
        <button onClick={() => handleClick(inputValue)}>Click me!</button>
    </div>
})

export default UseCallbackComponent;
