import { React, memo, useState, useEffect } from "react";

const MemoComponent = () => {
    const [products, setProduct] = useState([
        { title: 'this is a title', description: 'this is a description' },
        { title: 'this is a title', description: 'this is a description' },
        { title: 'this is a title', description: 'this is a description' },
        { title: 'this is a title', description: 'this is a description' },
        { title: 'this is a title', description: 'this is a description' },
        { title: 'this is a title', description: 'this is a description' },
        { title: 'this is a title', description: 'this is a description' },
        { title: 'this is a title', description: 'this is a description' },
        { title: 'this is a title', description: 'this is a description' },
        { title: 'this is a title', description: 'this is a description' },
        { title: 'this is a title', description: 'this is a description' }
    ]);

    const [counter, setCounter] = useState(0);

    const handleCounter = () => {
        setCounter(counter+1);
    }


    return <>   
        <header><button onClick={() => handleCounter()}>Click me</button>Increase Counter: {counter}</header>
        <h1>This isnt a good for perfromance</h1>

        {products.map((product) => <BadComponent product={product} />)}
    </>
};

const BadComponent = memo((prop) => {
    const { product } = prop;


    console.log('rerenders');

    return <div className="card">
        <span>{product.title}</span>
        <span>{product.description}</span>
    </div>
});


export default MemoComponent;
