import { memo, useCallback, useState } from "react";

const UseCallbackComponent = () => {
    const [counter, setCounter] = useState(0);
    const searchTerm = 'search';

    const handleClick = useCallback(() => {
        setCounter(counter + 1)

        console.log(counter)
    }, searchTerm)


    return <>
        <h1>useCallback</h1>
        <h2>Count {counter}</h2>
        <ChildComponent search={searchTerm} handleClick={handleClick}/>
    </>
}

const ChildComponent = memo(({ search, handleClick}) => {
    return <div>
        <button onClick={() => handleClick(search)}>Click me!</button>
    </div>
})

export default UseCallbackComponent;
