import { useCallback, useMemo, useState } from "react";

const UseCallbackComponent = () => {
    const [counter, setCounter] = useState(0);
    const handleClick = () => setCounter(counter + 1)

    const params = [35, 98];

    const calculateWithCallback = useCallback(() => {
        return param.forEach((el) => console.log('called on useCallback'));
    }, params)

    const calculateWithoutCallback = () => {
        return param.forEach((el) => console.log('called without useCallback'));
    }


    return <>
        <h1>useCallback</h1>
        <h2>Count {counter}</h2>
        <h3>Memoized calculation: {calculateWithCallback()}</h3>
        <h3>Memoized calculation: {}</h3>
        <button onClick={() => handleClick()}>Increase Counter</button>
    </>
}

export default UseCallbackComponent;
